package modulo11;

public class exTarde01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// conjunto dos numeros inteiros maiores do que zero e menores do que 10
		
		int[] inteiros = {1,2,3,4,5,6,7,8,9};
		
		char[] charac = {'a','b','c','d','e','f'};
		
		char[] shrek = new char[10];
		
		
		System.out.println(charac);
		System.out.println(shrek);
		
		for (int i = 0; i < (inteiros.length); i += 1) {
			System.out.println(inteiros[i]);
		}
	}

}
